import React, { Component } from 'react';
import * as actions from '../actions'
import { connect } from 'react-redux';
import { withRouter, Route } from 'react-router-dom';
import NotesNote from '../components/NotesNote';


class Notes extends Component {

  componentDidMount = () => {
    this.props.fetchNotes();
  }

  render() {

    return (
      <div class="app-container">
    <section id="panel">
    <div class="header" id="tabs">
      <ul>
        <li class="selected"><a href="/select"><span class="fa fa-file-text-o"></span><span class="tooltiptext">Notes</span></a></li>
        <li><a href="/search"><span class="fa fa-search"></span><span class="tooltiptext">Search</span></a></li>
        <li><a href="/tags"><span class="fa fa-tags"></span><span class="tooltiptext">Tags</span></a></li>
        <li><a href="/cog"><span class="fa fa-cog"></span><span class="tooltiptext">Preferences</span></a></li>
      </ul>
    </div>
    <div class="currentpanel" id="noteslist">
      <div id="tools">
        <div class="sort">Sort<span class="fa fa-caret-down"></span>
          <ul>
            <li>Title (ascending)</li>
            <li>Title (descending)</li>
            <li>Newest first</li>
            <li>Oldest first</li>
          </ul>
        </div>
        <div class="newnote">New Note</div>
      </div>
      <div class="body">
        <div class="note">
        <Route path="/notesview" render={props => (
        <NotesNote {...props} notes={this.props.notes} fetch={this.props.fetch} fetching={this.props.fetching} />
      )} />
        </div>

      </div>
    </div>
  </section>
  <section id="edit">
    <div class="header" id="tools">
      <ul id="toolbar">
        <li><span class="fa fa-bold"></span></li>
        <li><span class="fa fa-italic"></span></li>
        <li><span class="fa fa-underline"></span></li>
        <li><span>Font <span class="fa fa-caret-down"></span></span></li>
        <li><span class="fa fa-align-left"></span></li>
        <li><span class="fa fa-align-center"></span></li>
        <li><span class="fa fa-align-right"></span></li>
        <li><span class="fa fa-align-justify"></span></li>
      </ul>
      <div class="menu-toggle"><span class="fa fa-ellipsis-v"></span></div>
    </div>
    <div id="editor">
      <input placeholder="Add a title..."/>
      <div id="content" contenteditable="true" data-text="Enter text here"></div>
    </div>
  </section>
</div>

    );
  }
}

const mapStateToProps = state => {
    return {
      fetching: state.fetching,
      fetched: state.fetched,
      notes: state.notes,
      error: state.error,
    //   fetchingNote: state.fetchingNote,
    //   noteFetched: state.noteFetched,
    //   note: state.note,
    //   updating: state.updating,
    //   updated: state.updated,
    }
  }

  export default withRouter(connect(mapStateToProps, actions)(Notes));
