import React, { Component } from 'react';
import * as actions from '../actions'
import { connect } from 'react-redux';
import { withRouter, Route } from 'react-router-dom';
import NotesNote from '../components/NotesNote';
import NotesList from '../components/NotesList';
import Edit from '../components/Edit';



class Notes extends Component {
    constructor(props){
        super(props);
        this.state = {
            updateActive: false,
            modal: false,
            title: this.props.title,
            content: this.props.content
        }
    }

    toggleUpdate = () => {
        this.setState((prevState) => { 
            return {updateActive: !prevState.updateActive}
        });
    }

    toggleModal = () => {
        this.setState((prevState) => {
            return {modal: !prevState.modal};
        });
    }
     deleteNote = () => {
        this.props.deleteNote(this.props.match.params.id, this.props.history)

    }

  componentDidMount = () => {
    this.props.fetchNotes();
  }

  render() {

    return (
      <div class="app-container">
    <section id="panel">
    <div className="header" id="tabs">
      <ul>
        <li className="selected"><a href="/select"><span class="fa fa-file-text-o"></span><span className="tooltiptext">Notes</span></a></li>
        <li><a href="/search"><span className="fa fa-search"></span><span className="tooltiptext">Search</span></a></li>
        <li><a href="/tags"><span className="fa fa-tags"></span><span className="tooltiptext">Tags</span></a></li>
        <li><a href="/cog"><span className="fa fa-cog"></span><span className="tooltiptext">Preferences</span></a></li>
      </ul>
    </div>
    <div className="currentpanel" id="noteslist">
      <div id="tools">
        <div className="sort">Sort<span class="fa fa-caret-down"></span>
          <ul>
            <li>Title (ascending)</li>
            <li>Title (descending)</li>
            <li>Newest first</li>
            <li>Oldest first</li>
          </ul>
        </div>
        <div className="newnote">New Note</div>
      </div>
      <div className="body">
        <div className="note">
        <Route path="/notesview" render={props => (
        <NotesNote {...props} notes={this.props.notes} fetch={this.props.fetch} fetching={this.props.fetching} />
      )} />
      <div className="single-note-view">
                 <NotesList title={this.props.note.title} 
                content={this.props.note.textBody} />
                <button onClick={this.toggleUpdate} color="primary">Update</button>
                <button onClick={this.toggleModal} color="danger">Delete</button>

                {this.state.updateActive !== false
                    ? <Edit onCancel={this.toggleUpdate} 
                    title={this.props.note.title} 
                    content={this.props.note.textBody} 
                    updateNote={this.props.updateNote}
                     id={this.props.match.params.id} />
                    : null
                }

                </div>
        )
    }
}
        </div>

      </div>
    </div>
  </section>
  <section id="edit">
    <div className="header" id="tools">
      <ul id="toolbar">
        <li><span className="fa fa-bold"></span></li>
        <li><span className="fa fa-italic"></span></li>
        <li><span className="fa fa-underline"></span></li>
        <li><span>Font <span class="fa fa-caret-down"></span></span></li>
        <li><span className="fa fa-align-left"></span></li>
        <li><span className="fa fa-align-center"></span></li>
        <li><span className="fa fa-align-right"></span></li>
        <li><span className="fa fa-align-justify"></span></li>
      </ul>
      <div className="menu-toggle"><span class="fa fa-ellipsis-v"></span></div>
    </div>
    <div id="editor">
      <input placeholder= "Add a title..."/>
      <div id="content" contenteditable="true" data-text="Enter text here"></div>
    </div>
  </section>
</div>

    );
  }
}

const mapStateToProps = state => {
    return {
      fetching: state.fetching,
      fetched: state.fetched,
      notes: state.notes,
      error: state.error,
    //   fetchingNote: state.fetchingNote,
    //   noteFetched: state.noteFetched,
    //   note: state.note,
    //   updating: state.updating,
    //   updated: state.updated,

    }
  }

  export default withRouter(connect(mapStateToProps, actions)(Notes));
