import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter, Route, Link , Switch} from 'react-router-dom';
import * as actions from '../actions'
import NotesNote from '../components/NotesNote';
import NoteForm from './components/NoteForm';
import SingleNote from '../components/SingleNote';


class Notes extends Component {
  componentDidMount = () => {
    this.props.fetchNotes();
  }


  render() {
    return (
      <div className="App">
            <nav>
              <h1 tag={Link} to="/"><i className="fas fa-book-open"></i> &nbsp;View Notes </h1>
              <h1 tag={Link} to="/note/add"><i className="fas fa-plus"></i> &nbsp;New Note </h1>
              </nav>

      <div className='nav-bar'>
      <Route exact path="/" render={props => (
        <NotesNote {...props} notes={this.props.notes} fetch={this.props.fetch} fetching={this.props.fetching} />
      )} />
      <Switch>
      <Route path="/note/add" render={props => (
        <NoteForm {...props} addNote={this.props.addNote} />
      )} />
      <Route path="/notes/:id" render={props => (
          <SingleNote {...props} note={this.props.note} />
        )}  />
        </Switch>
        </div>
      </div>
    );
  }
}

// i think correcting the typo fixed my error!!!!!
const mapStateToProps = state => {
  return {
    fetching: state.fetching,
    fetched: state.fetched,
    notes: state.notes,
    error: state.error,
    fetchingNote: state.fetchingNote,
    noteFetched: state.noteFetched,
    note: state.note,
    updating: state.updating,
    updated: state.updated,
  }
}

export default withRouter(connect(mapStateToProps, actions)(Notes));
